import React from 'react'

const Search = evt => {

  if (evt.key === "Enter") {
    fetch(`${api.url}weather?q=${query}&units=metric&APPID=${api.key}`)
      .then(res => res.json())
      .then(result => {
        setWeather(result);
        setQuery('');
        console.log(result);
      });
  }
}

export default Search
